import React, {useEffect, useState} from 'react';
import {List, Avatar} from 'antd';

var pasEgal = 0;



const ListCrypto = () => {
	const [data, setData] = useState([]);
	const dataTmp = [...data];
	useEffect(() => {

		const ws = new WebSocket("wss://ws-sandbox.coinapi.io/v1/");
		ws.onopen = function (event) {
			ws.send(
				JSON.stringify({

					type: "hello",
					apikey: "F9065291-99B7-44B7-9F3C-4C02D0131B28",
					"heartbeat": false,
					"subscribe_data_type": ["trade", "quote", "book5"],
					"subscribe_filter_asset_id": ["BTC"]

				})
			);
			ws.onmessage = (message) => {
				const dataFromServer = JSON.parse(message.data);


				if (dataTmp.length === 0) {
					dataTmp.push(dataFromServer);
				} else {
					pasEgal = 0;
					for (var i = 0; i < dataTmp.length; i++) {
						if (dataTmp[i].symbol_id === dataFromServer.symbol_id) {
							dataTmp.splice(i, 1, dataFromServer);
							break;

						} else {
							pasEgal++;
						}

						if (pasEgal === dataTmp.length) {
							dataTmp.push(dataFromServer);

						}


					}

				}

				setData(dataTmp);
			};


		};

	}, []);
	return (

		<div>

			<List
				itemLayout="horizontal"
				dataSource={data}
				renderItem={item => (

					<List.Item>
						<List.Item.Meta
							avatar={<Avatar src="https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png"/>}
							title={<a href="/">{item.symbol_id.substr(12, 7)}</a>}
						/>

						ask_size: {item.ask_size}
						<br/>
						bid_price: {item.bid_price}
						<br/>
						bid_size: {item.bid_size}

					</List.Item>
				)}
			/>,
		</div>
	)
};

export default ListCrypto;
