import {createSlice, createAsyncThunk} from '@reduxjs/toolkit';
//const dataAssetsTmp = [];
//const dataAssetsLogoTmp = [];
const dataAssets5Tmp = [];
const dataAssetsLogo5Tmp = [];
var dataAssets5 = [];
const https = require('https');

export const fetchInfoCurrency = createAsyncThunk(
	'home/fetchCurrency',
	async () => {
//recuperer toutes les info crypto

		const request = await fetch('https://rest.coinapi.io/v1/assets', {
			method: 'GET',
			headers: {'X-CoinAPI-Key': 'F9065291-99B7-44B7-9F3C-4C02D0131B28'}
		});
		for (var i = 0; i < out.length; i++) {
			if ((out[i].asset_id === 'BTC') || (out[i].asset_id === 'ETH') || (out[i].asset_id === 'USDT') || (out[i].asset_id === 'XRP') || (out[i].asset_id === 'BCH')) {
				dataAssets5Tmp.push(out[i]);
			}
			console.log('data', dataAssets5Tmp);
			return dataAssets5Tmp ;
		}
		const data = await request.json();

		return data;


	}
);

export const currencySlice = createSlice({
	name: 'currency',
	initialState: {
		state: 'loading',
		error: null,
		currency: []
	},
	reducers: {
		hydrateCurrency: (state, action) => {
			state.currency = action.payload
		},
	},
	extraReducers: {
		[fetchInfoCurrency.pending]: (state, action) => {
			if (state.state !== 'loading') {
				state.state = 'loading';
			}
		},
		[fetchInfoCurrency.fulfilled]: (state, action) => {
			if (state.state === 'loading') {
				state.state = 'ready';
				state.currency = action.payload;
			}
		},
		[fetchInfoCurrency.rejected]: (state, action) => {
			if (state.state === 'loading') {
				state.state = 'error';
				state.error = action.error.message;

			}
		}

	}
});


export const getHomeCurrency = state => state.currency.currency;

export const {hydrateCurrency} = currencySlice.actions;

export default currencySlice.reducer;
