export const currencySlice = createSlice({
	name: 'currency',
	initialState: {
		state: 'loading',
		error: null,
		currency: []
	},
	reducers: {
		hydrateCurrency: (state, action) => {
			state.currency = action.payload
		},
	},
	extraReducers: {
		[fetchInfoCurrency.pending]: (state, action) => {
			if (state.state !== 'loading') {
				state.state = 'loading';
			}
		},
		[fetchInfoCurrency.fulfilled]: (state, action) => {
			if (state.state === 'loading') {
				state.state = 'ready';
				state.currency = action.payload;
			}
		},
		[fetchInfoCurrency.rejected]: (state, action) => {
			if (state.state === 'loading') {
				state.state = 'error';
				state.error = action.error.message;

			}
		}

	}
});


export const getHomeCurrency = state => state.currency.currency;

export const {hydrateCurrency} = currencySlice.actions;

export default currencySlice.reducer;
