import {createSlice} from '@reduxjs/toolkit';



export const currencySlice = createSlice({
	name: 'currency',
	initialState: {
		state: 'loading',
		error: null,
	},
	reducers: {
		changeState: (state, action) => {
			state.state = action.payload;
		},
	}
});



export default curerncySlice.reducer;
