import React, {useEffect} from 'react';
import {Link} from 'react-router-dom'
import {useDispatch, useSelector} from "react-redux";
import { Box, Card, CardActions, CardContent} from "@material-ui/core";
import {Alert, Skeleton} from '@material-ui/lab'
import {fetchBlogArticles, getHomeArticles, getHomeState} from "./homeSlice";


const Home = () => {
	const dispatch = useDispatch();
	const state = useSelector(getHomeState);

	const articles = useSelector(getHomeArticles);

	useEffect(() => {
		dispatch(fetchBlogArticles());
	}, []);
	return (
		<>
			<h1>Home</h1>

			{state === 'error' && (
				<Alert severity="error">Erreur lors du chargement des Crypto</Alert>
			)}
			{state === 'loading' && (
				<>
					<Box my={4}>
						<Skeleton animation="pulse" variant="rect" style={{height: 100}}/>
					</Box>
					<Box my={4}>
						<Skeleton animation="pulse" variant="rect" style={{height: 100}}/>
					</Box>
					<Box my={4}>
						<Skeleton animation="pulse" variant="rect" style={{height: 100}}/>
					</Box>

				</>
			)}
			{state === 'ready' && (

				<>
					{articles.map(item => (
						<Box key={item._id} my={4}>
							<Card>
								<CardContent>
									<h2>
										{item.title}
										<small style={{marginLeft: 20}}>{item.author.email}</small>
									</h2>
									<p>{item.description}</p>
								</CardContent>
								<CardActions>

								</CardActions>
							</Card>
						</Box>
					))}
				</>
			)}
		</>
	)
};
export default Home;
